SELECT r.*,visual_feature.attr_value visual_feature,microscope_feature.attr_value microscope_feature,path_diagnosis.attr_value path_diagnosis,submit_material.attr_value submit_material, s0.attr_value s0,s1.attr_value s1,s2.attr_value s2,s3.attr_value s3,s4.attr_value s4,s5.attr_value s5,s6.attr_value s6,s7.attr_value s7,s8.attr_value s8, m.doctor_id base_doctor_id,m.share_id,m.medical_conclusion,d.doctor_teleno,d.doctor_name,d.doctor_avatar,h.hospital_name,rh.hospital_name report_hosp_name FROM t_report r LEFT JOIN t_report_value visual_feature ON r.report_id = visual_feature.report_id and visual_feature.attr_id = 4 LEFT JOIN t_report_value microscope_feature ON r.report_id = microscope_feature.report_id and microscope_feature.attr_id = 5 LEFT JOIN t_report_value path_diagnosis ON r.report_id = path_diagnosis.report_id and path_diagnosis.attr_id = 6 LEFT JOIN t_report_value s0 ON r.report_id = s0.report_id and s0.attr_id = 195 LEFT JOIN t_report_value submit_material ON r.report_id = submit_material.report_id and submit_material.attr_id = 196
LEFT JOIN t_report_value s1 ON r.report_id = s1.report_id and s1.attr_id = 138 LEFT JOIN t_report_value s2 ON r.report_id = s2.report_id and s2.attr_id = 139 LEFT JOIN t_report_value s3 ON r.report_id = s3.report_id and s3.attr_id = 142 LEFT JOIN t_report_value s4 ON r.report_id = s4.report_id and s4.attr_id = 149 LEFT JOIN t_report_value s5 ON r.report_id = s5.report_id and s5.attr_id = 198 LEFT JOIN t_report_value s6 ON r.report_id = s6.report_id and s6.attr_id = 179 LEFT JOIN t_report_value s7 ON r.report_id = s7.report_id and s7.attr_id = 182 LEFT JOIN t_report_value s8 ON r.report_id = s8.report_id and s8.attr_id = 215
LEFT JOIN t_medical_record m on r.record_id = m.record_id LEFT JOIN t_doctor d on r.doctor_id = d.doctor_id LEFT JOIN t_hospital h on r.hospital_id = h.hospital_id LEFT JOIN t_hospital rh on r.report_hosp_id = rh.hospital_id WHERE r.report_type NOT IN (1,4,23);
